import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

// import App from "./App";

function reducer(state, action) {
  if (action.type === "increment") {
    return {
      count: state.count + state.step,
      step: state.step,
    };
  } else if (action.type === "decrement") {
    return {
      count: state.count - state.step,
      step: state.step,
    };
  } else if (action.type === "reset") {
    return {
      count: 0,
      step: state.step,
    };
  } else if (action.type === "updateStep") {
    return {
      count: state.count,
      step: action.step,
    };
  } else {
    throw new Error();
  }
}

function Counter() {
    const [state, dispatch] = React.useReducer(reducer, { count: 0, step: 1 });

  return (
    <>
      <Slider
        onChange={(value) =>
          dispatch({
            type: "updateStep",
            step: value,
          })
        }
      />
      <hr />
      <h1>{state.count}</h1>
      <button
        onClick={() =>
          dispatch({
            type: "increment",
          })
        }
      >
        +
      </button>
      <button
        onClick={() =>
          dispatch({
            type: "decrement",
          })
        }
      >
        -
      </button>
      <button
        onClick={() =>
          dispatch({
            type: "reset",
          })
        }
      >
        Reset
      </button>
    </>
  );
}

export default function Slider({ onChange }) {
  const [value, setValue] = React.useState(1);

  return (
    <>
      {value}
      <input
        type="range"
        min="1"
        max="10"
        value={value}
        onChange={(e) => {
          const value = Number(e.target.value);
          onChange(value);
          setValue(value);
        }}
      />
    </>
  );
}

ReactDOM.render(<Counter />, document.querySelector("#root"));
