import styled from "styled-components";

export const Box = styled.div `
    display: grid;
    grid-template-columns: repeat(3, auto);
    grid-template-rows: repeat(4, auto);
    grid-column-gap: 5px;
    grid-row-gap: 5px;
    padding: 10px;
    box-shadow: 0 0 20px 8px #d0d0d0;
    border-radius: 5px;

    img{
        width: 100%;
    }

`

export const oneItem = styled.div `
    grid-column: 1 / 3;
`
